<script>
    document.addEventListener('alpine:init', () => {

        Alpine.data('darkMode', () => ({
            isDark: false,
 
            init() {
                this.$watch('isDark', () => {
                   document.documentElement.classList.toggle('dark')
                })
            }
        }))
    })
</script>